import React, { useEffect, useState } from "react"
import axios from "axios"
import { ThemeItem } from "./components/theme/ThemeItem"


function App() {
  const [themes, setThemes] = useState([
    {id:1, title:"Title1", description: "Desc1"},
    {id:2, title:"Title2", description: "Desc2"},
    {id:3, title:"Title3", description: "Desc3"},
    {id:4, title:"Title4", description: "Desc4"},
  ])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('https://localhost:7172/api/theme/')
        console.log(response)
        setThemes(response.data)
      } catch (err) {
        setError(err.message)
      } finally {
        setLoading(false)
      }
    }

  }, [])
  return (
    <div className="App" style={{display: "flex", flexWrap: "wrap", gap: "2rem"}}>
      {themes.map(theme => {
        })
      }
    </div>
  );
}

export default App;
