function App() {
  // 1. Стан для зберігання даних
  const [users, setUsers] = useState([])

  // 2. Стан для відображення процесу завантаження
  const [loading, setLoading] = useState(true)
  // 3. Стан для помилок
  const [error, setError] = useState(null)

  // useEffect спрацює один раз при завантаженні компонента (через пустий масив [])
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Робимо запит
        const response = await axios.get('https://jsonplaceholder.typicode.com/users')

        // Зберігаємо дані у стан (axios ховає дані у властивість .data)
        setUsers(response.data)
      } catch (err) {
        // Якщо помилка - записуємо її
        setError(err.message)
      } finally {
        // У будь-якому випадку прибираємо "Завантаження"
        setLoading(false)
      }
    }

    fetchData()
  }, [])
  return (
    <div className="App">

    </div>
  );
}

export default App;
