import { useMemo } from "react";

export const useSortedThemes = (themes, sort) => { 
  const sortedThemes = useMemo(() =>{

    if(sort){
      return [...themes].sort((a, b) => a[sort].localeCompare(b[sort]))
    }
    return themes;
  }, [sort, themes]);
  return sortedThemes;
}

export const useThemes = (sortedThemes, filter) => {
    
  const sortedAndSearchedThemes = useMemo(() =>{
    return sortedThemes.filter(theme => theme.title.toLowerCase().includes(filter.query.toLowerCase()))
  }, [filter.query, sortedThemes]);
  return sortedAndSearchedThemes;
}
