import React, { useEffect, useState } from "react"
import axios from "axios"
import { ThemeItem } from "./components/theme/ThemeItem"

import "./styles/theme/ThemeItem.css"

function App() {
  const [themes, setThemes] = useState([

    { id: 1, title: "Daily Routine", description: "Слова про розпорядок дня: wake up, brush teeth, go to work." },
    { id: 2, title: "Food & Cooking", description: "Назви продуктів, кухонного приладдя та дієслова приготування їжі." },
    { id: 3, title: "Travel & Airport", description: "Лексика для подорожей: check-in, boarding pass, luggage." },
    { id: 4, title: "Family & Relatives", description: "Назви членів родини та родичів: nephew, mother-in-law, siblings." },
    { id: 5, title: "Emotions & Feelings", description: "Як описати свій настрій: exhausted, thrilled, anxious, relieved." },
    { id: 6, title: "Weather Forecast", description: "Опис погоди: drizzle, pouring rain, blizzard, scorching hot." },
    { id: 7, title: "Clothes & Fashion", description: "Предмети одягу, тканини та стилі: denim, leather, striped, baggy." },
    { id: 8, title: "Very Long Vocabulary Topic Title To Test Text Overflow Ellipsis In Your Component", description: "Тестовий опис для перевірки CSS стилів." },
    { id: 9, title: "Health & Medicine", description: "Симптоми та лікування: headache, flu, prescription, sore throat." },
    { id: 10, title: "Jobs & Professions", description: "Назви професій та робочих обов'язків: plumber, accountant, nurse." },
    { id: 11, title: "House & Furniture", description: "Кімнати та меблі: armchair, wardrobe, cozy, attic, basement." },
    { id: 12, title: "Phrasal Verbs (Get)", description: "Фразові дієслова: get along, get over, get away, get up." },
    { id: 13, title: "City & Directions", description: "Орієнтування в місті: roundabout, crosswalk, straight ahead." },
    { id: 14, title: "Hobbies & Interests", description: "Чим зайнятися у вільний час: hiking, knitting, photography." },
    { id: 15, title: "Animals & Wildlife", description: "Дикі та домашні тварини: squirrel, hedgehog, whale, parrot." },
    { id: 16, title: "Education & School", description: "Шкільна лексика: curriculum, principal, assignment, degree." },
    { id: 17, title: "Shopping & Money", description: "Покупки: bargain, receipt, refund, discount, cash register." },
    { id: 18, title: "Sport & Fitness", description: "Види спорту та вправи: push-ups, treadmill, referee, score." },
    { id: 19, title: "Movies & Cinema", description: "Жанри та кіноіндустрія: plot twist, cast, sequel, blockbuster." },
    { id: 20, title: "Character Traits", description: "Риси характеру: stubborn, generous, clumsy, reliable." },
    { id: 21, title: "Phrasal Verbs (Look)", description: "Фразові дієслова: look after, look for, look forward to." },
    { id: 22, title: "Technology & Gadgets", description: "Сучасні пристрої: charger, wireless, scroll, touchscreen." },
    { id: 23, title: "Environment", description: "Екологія: pollution, global warming, recycling, waste." },
    { id: 24, title: "Crime & Law", description: "Юридичні терміни: judge, witness, theft, guilty, lawyer." },
    { id: 25, title: "Idioms about Time", description: "Ідіоми: once in a blue moon, kill time, against the clock." },
    { id: 26, title: "Business English", description: "Офісна лексика: negotiation, deadline, meeting, salary." },
    { id: 27, title: "Colors & Shapes", description: "Відтінки та форми: turquoise, beige, rectangle, sphere." },
    { id: 28, title: "Music & Instruments", description: "Музичні інструменти та стилі: choir, lyrics, rhythm, drums." },
    { id: 29, title: "Body Parts", description: "Частини тіла: wrist, ankle, forehead, stomach, muscle." },
    { id: 30, title: "Emergency", description: "Екстрені ситуації: ambulance, fire engine, rescue, help." }
  ]);

  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('https://localhost:7172/api/theme/')
        console.log(response)
        setThemes(response.data)
      } catch (err) {
        setError(err.message)
      } finally {
        setLoading(false)
      }
    }

  }, [])
  return (
    <div className="App">
        <h1 style={{textAlign: "center"}}>
           Themes
        </h1>
        <div className="themes_list">
      {themes.map(theme => {
        return <ThemeItem theme = {theme} key={theme.id}/>
      })}
      </div>
    </div>
  );
}

export default App;
