import React, { useEffect, useMemo, useState } from "react"
import axios from "axios"
import "./styles/App.css"
import { ThemeList } from "./components/theme/ThemeList";
import { ThemeForm } from "./components/theme/ThemeForm";
import ThemeFilter from "./components/theme/ThemeFilter";
import SimpleModal from "./components/UI/SimpleModal/SimpleModal";
import Button from "./components/UI/button/Button";
import { useThemes } from "./hooks/useFilter";
import ThemeService from "./API/ThemeService";
import Loader from "./components/UI/Loader/Loader";
import useFetching from "./hooks/useFetching";

function App() {
  const [themes, setThemes] = useState([  ]);

  const [error, setError] = useState(null)
  const [isSending, setIsSending] = useState()
  const [filter, setFilter] = useState({sort: '', query: ''})
  const [newTheme, setNewTheme] = useState({title:'', description:''})
  const [modalVisible, setModalVisible] = useState(false) 
  const sortedAndSearchedThemes = useThemes(themes, filter);
  const [isLoading, setIsLoading] = useState(false)
  const [themeError, setThemeError] = useState(null)
  const [getDataThemes, isLoadingGet, themeErrorGet] = useFetching( async () => {
    const data = await ThemeService.getAll();
    setThemes(data);
  })
  const [CreateTheme, isLoadingCreate, themeErrorCreate] = useFetching( async () => {
    const data = await ThemeService.create(newTheme);
    setThemes(data);
  })

  useEffect(() => {

    getDataThemes();
    setIsLoading(isLoadingGet);
    setThemeError(themeErrorGet);
  }, [])

  const addNewTheme = async () =>{
    if (!newTheme) return alert("Заповніть назву теми!")
    setIsSending(true)
    CreateTheme();
    setIsLoading(isLoadingCreate)
    setError(themeErrorCreate)
    setModalVisible(false)
    setNewTheme({title:'', description:''})
    }
  const deleteTheme = async (id) => {
    try {
      await axios.delete(`https://localhost:7172/api/theme/${id}`);
      getDataThemes();
    } catch (err) {
      console.error("Помилка при видаленні:", err);
      alert("Не вдалося видалити тему");
    }
  }

  return (
    <div className="App">
       <SimpleModal visible={modalVisible} setVisible={setModalVisible}>  
       <ThemeForm create={addNewTheme} newTheme={newTheme} setNewTheme={setNewTheme}/>
        </SimpleModal>
        <Button onClick={() => setModalVisible(true)}>
          Create new theme
        </Button>
       <hr style={{margin: "1rem 0 1rem 0"}}></hr>
       <ThemeFilter filter={filter} setFilter={setFilter}/>
       {themeError && <div style={{color: "red"}}>{themeError}</div>}
       {isLoading
        ? <div style={{display: "flex", justifyContent: "center", marginTop: "3rem"}}>
            <Loader/>
          </div>
        : 
        <ThemeList themes={sortedAndSearchedThemes} title={"Themes"} deleteTheme={deleteTheme}/>
       }


    </div>
  );
}

export default App;
