import React, { useEffect, useState } from "react"
import axios from "axios"
import { ThemeItem } from "./components/theme/ThemeItem"
import "./styles/App.css"
import { ThemeList } from "./components/theme/ThemeList";
import { Button } from "./components/UI/button/Button";
import Input from "./components/UI/input/Input";

function App() {
  const [themes, setThemes] = useState([

  ]);

  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [isSending, setIsSending] = useState()

  const [title, setTitle] = useState()
  const [description, setDescription] = useState()

  const [newTheme, setNewTheme] = useState({title:'', description:''})
  const fetchDataThemes = async () => {
    try {
      const response = await axios.get('https://localhost:7172/api/theme/')
      setThemes(response.data.themes)
    } catch (err) {
      setError(err.message)
    } finally {
      setLoading(false)
    }
  }
  useEffect(() => {
    fetchDataThemes();
  }, [])

  const addNewTheme = async (e) =>{
    e.preventDefault()
    if (!title) return alert("Заповніть поля!")
    setIsSending(true)

    try{

      const response = await axios.post('https://localhost:7172/api/theme/', newTheme);

      console.log("Успішно створено:", response.data)
      setTitle('')
      setDescription('')
    } catch (err) {
      console.error("Помилка при створенні:", err)
      alert("Не вдалося створити тему")
    } finally {
        setIsSending(false)
        fetchDataThemes()
      }

    }

  return (
    <div className="App">
      <form>
        <Input 
        value = {newTheme.title}
        onChange = {e => setTheme({...theme, title: e.target.value})}
        placeholder="Title of new theme"/>
        <br/>
        <Input 
        value = {theme.description}
        onChange = {e=> setTheme({...theme, description: e.target.value})}
        placeholder="Descrition of new theme"/>
        <br/>
        <Button onClick = {addNewTheme}>
          Create
        </Button>
      </form>

       {themes.length !== 0
       ? <ThemeList themes={themes} title={"Themes"}/>
       : <h1>List of themes is empty</h1>
       }

    </div>
  );
}

export default App;
