import React, { useEffect, useMemo, useState } from "react"
import axios from "axios"
import "./styles/App.css"
import { ThemeList } from "./components/theme/ThemeList";
import { ThemeForm } from "./components/theme/ThemeForm";
import Select from "./components/UI/select/Select";
import Input from "./components/UI/input/Input";


function App() {
  const [themes, setThemes] = useState([

  ]);

  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [isSending, setIsSending] = useState()
  const [selectedSort, setSelectedSort] = useState('')
  const [searchQuery, setSearchQuery] = useState('')
  const [newTheme, setNewTheme] = useState({title:'', description:''})


  const sortedThemes = useMemo(() =>{

    if(selectedSort){
      console.log("Selected sort:", searchQuery);
      return [...themes].sort((a, b) => a[selectedSort].localeCompare(b[selectedSort]))
    }
    return themes;
  }, [selectedSort, themes]);

  const sortedAndSearchedThemes = useMemo(() =>{
    return sortedThemes.filter(theme => theme.title.toLowerCase().includes(searchQuery.toLowerCase()))
  }, [searchQuery, sortedThemes]);
  const fetchDataThemes = async () => {
    try {
      const response = await axios.get('https://localhost:7172/api/theme/')
      setThemes(response.data.themes)
    } catch (err) {
      setError(err.message)
    } finally {
      setLoading(false)
    }
  }
  useEffect(() => {
    fetchDataThemes();
  }, [])

  const addNewTheme = async () =>{
    if (!newTheme) return alert("Заповніть назву теми!")
    setIsSending(true)

    try{

      const response = await axios.post('https://localhost:7172/api/theme/', newTheme);

      console.log("Успішно створено:", response.data)
      setNewTheme({title:'', description:''})
    } catch (err) {
      console.error("Помилка при створенні:", err)
      alert("Не вдалося створити тему")
    } finally {
        setIsSending(false)
        fetchDataThemes()
      }

    }
  const deleteTheme = async (id) => {
    try {
      await axios.delete(`https://localhost:7172/api/theme/${id}`);
      fetchDataThemes();
    } catch (err) {
      console.error("Помилка при видаленні:", err);
      alert("Не вдалося видалити тему");
    }
  }

  const sortThemes = (sort) => {
    setSelectedSort(sort);
  }


  return (
    <div className="App">

       <ThemeForm create={addNewTheme} newTheme={newTheme} setNewTheme={setNewTheme}/>
       <hr style={{margin: "1rem 0 1rem 0"}}></hr>
       <div>
        <Input placeholder="Search themes..."
        value={searchQuery} onChange={e => setSearchQuery(e.target.value)} />
        <br/>
        <Select value={selectedSort} onChange={sortThemes} defaultValue="Sort by..." options={[
          {value: 'title', name: 'By Title'},
          {value: 'description', name: 'By Description'}
        ]}/>
       </div>
       {themes.length !== 0
       ? <ThemeList themes={sortedAndSearchedThemes} title={"Themes"} deleteTheme={deleteTheme}/>
       : <h1>List of themes is empty</h1>
       }

    </div>
  );
}

export default App;
