import React from 'react'

const useFetching = (callback, [isLoading,  setIsLoading], [error, setError]) => {
  

  const fetching = async () => {
    try {
      setIsLoading(true);
      await callback();
    } catch (e) {
      setError(e.message);
    } finally {
      setIsLoading(false);
    }
  };

  return [fetching, isLoading, error];
}
export default useFetching
