import React, { useEffect, useState } from "react"
import axios from "axios"
import { ThemeItem } from "./components/theme/ThemeItem"


function App() {
  const [themes, setThemes] = useState([
    { id: 1, title: "JavaScript Basics", description: "Основи мови програмування JavaScript, змінні та функції." },
    { id: 2, title: "React Hooks", description: "Вивчення useState, useEffect та інших хуків для керування станом." },
    { id: 3, title: "CSS Flexbox", description: "Як вирівнювати елементи на сторінці за допомогою гнучких контейнерів." },
    { id: 4, title: "HTML5 Semantic", description: "Правильна структура веб-сторінки: header, main, footer, article." },
    { id: 5, title: "Node.js Server", description: "Створення простого бекенду на JavaScript." },
    { id: 6, title: "Asynchronous JS", description: "Розуміння промісів, async/await та event loop." },
    { id: 7, title: "Дуже довгий заголовок, який точно не влізе в картку і має обрізатися", description: "Короткий опис." },
    { id: 8, title: "Git & GitHub", description: "Системи контролю версій для командної розробки." },
    { id: 9, title: "Redux Toolkit", description: "Керування складним глобальним станом у великих додатках." },
    { id: 10, title: "TypeScript", description: "Додавання статичної типізації до JavaScript коду." },
    { id: 11, title: "Docker Containers", description: "Основи контейнеризації додатків для деплою." },
    { id: 12, title: "SQL Databases", description: "Робота з реляційними базами даних, PostgreSQL та MySQL." },
    { id: 13, title: "MongoDB NoSQL", description: "Робота з документо-орієнтованими базами даних." },
    { id: 14, title: "REST API Design", description: "Архітектурний стиль взаємодії компонентів розподіленого додатка." },
    { id: 15, title: "GraphQL", description: "Мова запитів для API та середовище виконання для цих запитів." },
    { id: 16, title: "Next.js Framework", description: "Фреймворк для React з підтримкою Server Side Rendering." },
    { id: 17, title: "Vue.js", description: "Інший популярний фреймворк. Прогресивний фреймворк JavaScript." },
    { id: 18, title: "Angular", description: "Платформа для розробки мобільних і настільних веб-додатків." },
    { id: 19, title: "Svelte", description: "Компілятор, який перетворює декларативні компоненти на ефективний JS." },
    { id: 20, title: "WebSockets", description: "Технологія для відкриття постійного з'єднання між клієнтом і сервером." },
    { id: 21, title: "Testing with Jest", description: "Фреймворк для тестування JavaScript коду." },
    { id: 22, title: "Cypress E2E", description: "Інструмент для наскрізного тестування веб-додатків." },
    { id: 23, title: "CI/CD Pipelines", description: "Безперервна інтеграція та доставка коду." },
    { id: 24, title: "Linux Basics", description: "Основи роботи в терміналі Linux для розробників." },
    { id: 25, title: "Algorthims", description: "Сортування, пошук та інші базові алгоритми." },
    { id: 26, title: "Data Structures", description: "Масиви, списки, дерева, графи та хеш-таблиці." },
    { id: 27, title: "Design Patterns", description: "Singleton, Factory, Observer та інші патерни проектування." },
    { id: 28, title: "Clean Code", description: "Принципи написання коду, який легко читати та підтримувати." },
    { id: 29, title: "Web Security", description: "Захист від XSS, CSRF та інших вразливостей." },
    { id: 30, title: "Performance", description: "Оптимізація швидкості завантаження веб-сторінок." }
  ])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('https://localhost:7172/api/theme/')
        console.log(response)
        setThemes(response.data)
      } catch (err) {
        setError(err.message)
      } finally {
        setLoading(false)
      }
    }

  }, [])
  return (
    <div className="App" style={{display: "flex", flexWrap: "wrap", gap: "2rem"}}>
      {themes.map(theme => {
        console.log(theme.title);
        return <ThemeItem theme = {theme} key={theme.id}/>
      })}
    </div>
  );
}

export default App;
